// import { SELECTED_REFPOST, ALL_REFPOSTS, SHOW_ERROR } from '../actions/refPost-actions'

const userReducer = (state = null, {type, payload}) => {
  switch(type) {
    case UPDATE_USER:
      return {
        email: payload.email,
        token: token
      };
    default: return state
  }
}

export default userReducer
