import Layout from './Layout'
import { createSelector } from 'reselect'
import { connect } from 'react-redux'

import { loginUser } from '../redux/actions/user-actions'

class Login extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      email: null,
      password: null
    }
  }

  onHandleInputChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  onHandleLogin = (event) => {
    event.preventDefault()
    console.log(this.props)
    this.props.onLoginUser(this.state.email, this.state.password);
    console.log(this.props.user)
  }

  render() {
    return(
      <form onSubmit={this.onHandleLogin}>
        <input onChange={this.onHandleInputChange} name="email" type="email"/>
        <input onChange={this.onHandleInputChange} name="password" type="password"/>
        <button type="submit">Login</button>
      </form>
    )
  }
}

const userSelector = createSelector(
  state => state.user,
  user => user
)

const mapStateToProps = createSelector(
  userSelector,
  (user) => ({
    user
  })
);

const mapActionsToProps = {
  onLoginUser: loginUser
}

export default Layout(connect(mapStateToProps, mapActionsToProps)(Login))
