import Layout from './Layout'
import { createSelector } from 'reselect'
import { connect } from 'react-redux'

import { loginUser, logoutUser } from '../redux/actions/user-actions'

class Login extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      email: null,
      password: null,
      formType: 'login'
    }
  }

  onHandleInputChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  onHandleLogin = (event) => {
    event.preventDefault()
    this.props.onLoginUser(this.state.email, this.state.password);
  }

  changeFormToSignUp = () => {
    this.setState({
      formType: 'signup'
    })
  }

  changeFormToLogin = () => {
    this.setState({
      formType: 'login'
    })
  }

  test = () => {
    console.log(this.props.user)
  }

  render() {
    return(
      <div>
        {this.state.formType === 'login' &&
          <form onSubmit={this.onHandleLogin}>
            <h1>Login Form</h1>
            <input onChange={this.onHandleInputChange} name="email" type="email"/>
            <input onChange={this.onHandleInputChange} name="password" type="password"/>
            <button type="submit">Login</button>
            <button onClick={this.changeFormToSignUp}>Change to Signup</button>
            <button onClick={this.props.onLogoutUser()}>Logout</button>
          </form>
        }
        {this.state.formType === 'signup' &&
          <form onSubmit={this.onHandleLogin}>
            <h1>Signup Form</h1>
            <input onChange={this.onHandleInputChange} name="email" type="email"/>
            <input onChange={this.onHandleInputChange} name="password" type="password"/>
            <button type="submit">Signup</button>
            <button onClick={this.changeFormToLogin}>Change to Login</button>
            <button onClick={this.props.onLogoutUser()}>Logout</button>
          </form>
        }
        <button onClick={this.test}>test</button>
      </div>
    )
  }
}

const userSelector = createSelector(
  state => state.user,
  user => user
)

const mapStateToProps = createSelector(
  userSelector,
  (user) => ({
    user
  })
);

const mapActionsToProps = {
  onLoginUser: loginUser,
  onLogoutUser: logoutUser
}

export default Layout(connect(mapStateToProps, mapActionsToProps)(Login))
